% run PCA over train set% Initializationclear ; close all; clcdimension = 2700;X_all = [];Z = [];for i = 0 : 18  filename = strcat("../GetFeatures/GetFeatures/results_script/results_161113/feat_data/x_feat_",num2str(i),".txt");  X = load(filename);  X_all = [X_all;X];end[U, S] = pca(X_all);% check variance retaining percentage, should be over 0.99retain_perc = sum(sum(S(:,1:dimension)))/sum(sum(S));fprintf("PCA with dimension : %i, variance retaining percentage : %d\n",dimension,retain_perc);% Dimension ReductionZ = projectData(X_all, U, dimension);  save "input/PCA_training_set.txt" Z