%run PCA over train set% Initializationclear ; close all; clcdimension = 1000;Z_out = [];for i = 0 : 3  filename = strcat("input/x_feat_",num2str(i),".txt");  fprintf('PCA over %s...\n', filename);  X = load(filename);  [U, S] = pca(X);  % check variance retaining percentage, should be over 0.99  retain_perc = sum(sum(S(:,1:dimension)))/sum(sum(S));  fprintf("PCA with dimension : %i, variance retaining percentage : %d\n",dimension,retain_perc);  % Dimension Reduction  Z = projectData(X, U, dimension);    Z_out = [Z_out; Z];endsave PCA_training_set.txt Z_out